@pytest.mark.usefixtures("unittest_setUpClass_fixture_Upload_Image")
class Upload_Image(HomePage,LoginPage):

    # Validate user is able open the SDP page via global search bar.
    @pytest.mark.ImageUpload
    @pytest.mark.mutualfund
    @pytest.mark.Android
    def test_uploadImageInMutualFund(self):
        self.login_and_verify_homepage_for_reg_user(user_data['reg_no'])

        for i in range(20):
            self.click(rekshadhana)
            self.sleep(3)
            self.click(open_MF)
            self.sleep(2)
            self.click(buy_mf)
            self.click(tingat_pop)
            self.click(price)
            self.click(buy_btn_price)
            self.click(pilih)
            self.click(bank)
            self.click(checkbox)
            self.click(buyer)
            self.click(ok_btn)
            self.click(close_btn)
            self.click(trasaction_details)
            self.click(upload_image)
            self.click(from_gallery)
            self.click(allow)
            self.click(image)
            self.click(crop)
            self.sleep(4)
            self.assert_equal(self.get_attribute(verify_text, 'text'), text)
            self.go_back()
            self.click(Home)


